<script>
  import anime from 'animejs'

  export default {
    name: "index",
    mounted() {
      this.init()
    },
    updated() {
      this.init()
    },
    methods: {
      init() {
        var circleRotate = anime({
          targets: '.kopernik .circle',
          rotate: {
            value: function (el, i) {
              if (i === 0) return 720;
              else if (i === 1) return 1440;
              else return 1080
            },
            easing: 'linear',
            duration: function (el, i) {
              return 24000
            }
          },
          loop: true
        });

        var largeCirclePath = anime.path('.kopernik .large-circle-path');
        var largeCirclePathAnime = anime({
          targets: '.kopernik .large-circle-elements',
          strokeDashoffset: [anime.setDashoffset, 0],
          translateX: largeCirclePath('x'),
          translateY: largeCirclePath('y'),
          easing: 'linear',
          duration: 15000,
          loop: true
        });

        var mediumCirclePath = anime.path('.kopernik .medium-circle-path');
        var mediumCirclePathAnime = anime({
          targets: '.kopernik .medium-circle-elements',
          strokeDashoffset: [anime.setDashoffset, 0],
          translateX: mediumCirclePath('x'),
          translateY: mediumCirclePath('y'),
          easing: 'linear',
          duration: 7500,
          loop: true
        });

        var smallCirclePath = anime.path('.kopernik .small-circle-path');
        var smallCirclePathAnime = anime({
          targets: '.kopernik .small-circle-elements',
          strokeDashoffset: [anime.setDashoffset, 0],
          translateX: smallCirclePath('x'),
          translateY: smallCirclePath('y'),
          easing: 'linear',
          duration: 11250,
          loop: true
        });

        function showIcon(icon) {
          anime({
            targets: '.kopernik .icon_' + icon,
            opacity: 1,
            easing: 'linear',
            duration: 1000,
            direction: 'alternate'
          });
          setTimeout(() => {
            var index = iconsNow.indexOf(icon);
            iconsNow.splice(index, 1);
          }, 2500);

        }

        function showIconRandom() {
          var timerId = setInterval(() => {
            var iconNow;
            for (var i = 0; i < 10; i++) {
              iconNow = getRandomInt(1, 12);
              if (iconsNow.indexOf(iconNow) === -1) {
                break;
              }
            }
            iconsNow.push(iconNow);
            showIcon(iconNow);
          }, 1000);
          setTimeout(() => {
            clearInterval(timerId);
          }, 60000);
        }

        function getRandomInt(min, max) {
          return Math.floor(Math.random() * (max - min + 1)) + min;
        }

        var iconsNow = [];
        showIconRandom();
      }
    }
  }
</script>

